import { serverSideTranslations } from 'next-i18next/serverSideTranslations'
import nookies from 'nookies'
import axios from 'axios'

import { StatusCode } from '@/utils/StatusCode'
import { GetServerSidePropsContext } from 'next'

const useAuthMiddleware =
  (locales: string[] = []) =>
  async (context: GetServerSidePropsContext) => {
    const cookies = nookies.get(context)
    try {
      await axios.get(`${process.env.NEXT_PUBLIC_BACKEND_URL}health_check/protected`, {
        headers: { Authorization: `Bearer ${cookies.token}` }
      })
    } catch (error) {
      if (error?.response?.status === StatusCode.UNAUTHORIZED) {
        nookies.destroy(context, 'token')
        nookies.destroy(context, 'redirect_to')
        nookies.destroy(context, 'current_job_id')
        return {
          redirect: {
            destination: '/login'
          }
        }
      }
    }
    if (context.resolvedUrl === '/' && cookies.redirect_to) {
      return {
        redirect: {
          destination: cookies.redirect_to
        }
      }
    }
    return {
      props: {
        ...(await serverSideTranslations(context.locale, locales))
      }
    }
  }

export default useAuthMiddleware
