import { GetBackgroundJob } from '@/types/BackgroundJob'
import Http from '@/utils/Http'

const commonApi = {
  getInitData(request) {
    return Http.get('init_data', request)
  },
  startBackgroundJob(type: string, operation_data: any, file: File) {
    const formData = new FormData()
    const background_operation_data = JSON.stringify({ type, operation_data })
    formData.append('background_operation_data', background_operation_data)
    formData.append('background_operation_file', file)
    return Http.post<GetBackgroundJob>('background_operation/start', formData)
  },
  stopBackgroundJob(operation_id: string) {
    return Http.put('background_operation/stop', { operation_id })
  },
  sendReportMail(data) {
    return Http.post('send_report_mail', data)
  }
}
export default commonApi
