import * as yup from 'yup';
import _ from 'lodash';
import { itemMassFormat } from '@/utils/constant';

var getValidationSchema = function getValidationSchema(t) {
  return yup.object({
    job_id: yup.number().nullable().required(t('validation_message.job_id_required')),
    dpn: yup.string().required(t('validation_message.dpn_required')).matches(/^(h|H)\d{3}$/, t('validation_message.dpn_matches')).transform(function (value) {
      return _.toUpper(value);
    }),
    drawing_id: yup.mixed().test('drawing_required', t('validation_message.drawing_id_required'), function (value) {
      return _.isNull(value) === false;
    }),
    mass: yup.number().nullable().typeError(t('validation_message.mass_required')).required(t('validation_message.mass_required')).min(itemMassFormat.min, t('validation_message.mass_range')).max(itemMassFormat.max, t('validation_message.mass_range')),
    unit: yup.number().nullable().required(t('validation_message.unit_required'))
  });
};

export default getValidationSchema;
