import { useFormContext } from 'react-hook-form'
import { useSelector } from 'react-redux'

import { Grid } from '@material-ui/core'
import {
  DataTable,
  AppAutocompleteAsync,
  FormControllerTextField,
  SectionTimezone,
  AppAutocomplete
} from '@/components'
import { When } from 'react-if'

import { tagStore, commonStore } from '@/store/reducers'

import type { SpecificationDetail } from '@/types/Specification'
import type { DataForDropdown } from '@/types/Common'

const TabGeneral = () => {
  const specificationForm = useFormContext<SpecificationDetail>()

  const specificationDetail = useSelector(tagStore.selectDetail)
  const userJob = useSelector(commonStore.selectUserValueJob)

  const handleChangeDrawingId = (event, optionValue: DataForDropdown) => {
    console.warn('optionValue', '>>>', optionValue)
  }

  const columns = React.useMemo<GridColumns>(
    () => [
      {
        ...columnProperties.defaultProperties,
        field: 'description_raw',
        width: 130,
        headerName: 'Language',
        valueGetter(params) {
          return `${params.row.parameter_id} - ${params.row.description}`
        }
      },
      {
        ...columnProperties.defaultProperties,
        ...columnProperties.editCell('Description'),
        field: 'value_raw',
        flex: 0.7,
        headerName: 'Description',
        hide: editor !== 'text',
        valueGetter(params) {
          const cellValue = getCellValue(params.id as number)
          return cellValue
        },
        renderEditCell(params) {
          return (
            <DataTableTextField
              params={params}
              onChangeValue={onChangeDescription}
              rules={yup.string().max(255, 'Description must be less than 255 characters!')}
            />
          )
        }
      },
      {
        field: 'value_raw_editor',
        flex: 0.7,
        headerName: 'Description',
        sortable: false,
        hide: editor === 'text',
        renderCell(params) {
          const description = getCellValue(params.id as number)
          const pureContent = striptags(description)
          const htmlContent = parseHTML(_.toString(description))
          if (pureContent) {
            return (
              <Tooltip title={htmlContent}>
                <div className={classes.rteRoot} onDoubleClick={onOpenRTE(params.row)}>
                  {pureContent}
                </div>
              </Tooltip>
            )
          }
          return (
            <div className={classes.rteRoot} onDoubleClick={onOpenRTE(params.row)}>
              {pureContent}
            </div>
          )
        }
      }
    ],
    [descriptionValues, editor]
  )

  return (
    <Grid container spacing={2}>
      <Grid item xs={6}>
        <Grid container spacing={2}>
          <Grid item xs={12} style={{ marginTop: 8 }}>
            <AppAutocomplete
              helperText={specificationForm.errors.job_id?.message}
              error={Boolean(specificationForm.errors.job_id)}
              value={userJob}
              popupIcon={null}
              disabled
              label="Job"
              required
              primaryKeyOption="value"
              options={[]}
              renderOption={(option) => option.description}
            />
          </Grid>
          <Grid item xs={12}>
            <AppAutocompleteAsync
              helperText={specificationForm.errors.drawing_id?.message}
              error={Boolean(specificationForm.errors.drawing_id)}
              // value={specificationDetail.drawing_id}
              compName="drawing_list"
              additionalData={{ limit_to_job: userJob.value }}
              required
              label="Part #"
              onChange={handleChangeDrawingId}
            />
          </Grid>

          <Grid item xs={12}>
            <FormControllerTextField
              disabled={false}
              control={specificationForm.control}
              label="Description #"
              required
              name="spec_id"
            />
          </Grid>

          <Grid item xs={6}>
            <FormControllerTextField
              disabled={false}
              control={specificationForm.control}
              label="Mass kg"
              required
              name="spec_id"
            />
          </Grid>

          <Grid item xs={6}>
            <FormControllerTextField
              disabled={false}
              control={specificationForm.control}
              label="Unit #"
              name="spec_id"
            />
          </Grid>

          <When condition={false}>
            <SectionTimezone value={specificationDetail} />
          </When>
        </Grid>
      </Grid>

      <Grid item xs={6}>
        <DataTable
          hideFooter
          disableSelectionOnClick
          disableColumnMenu
          tableHeight={tableHeight}
          rows={filteredLanguageList}
          columns={columns}
          onCellDoubleClick={onDbClickCell}
          getRowClassName={getStylesRowDisabled}
        />
      </Grid>
    </Grid>
  )
}

export default TabGeneral
