import { useFormContext } from 'react-hook-form'
import { useSelector } from 'react-redux'

import { Grid } from '@material-ui/core'
import {
  TableMultilingualDescription,
  AppAutocompleteAsync,
  FormControllerTextField,
  AppAutocomplete
} from '@/components'

import { materialStore, commonStore } from '@/store/reducers'

import type { MaterialDetail } from '@/types/'
import type { DataForDropdown } from '@/types/Common'

const TabGeneral = () => {
  const materialForm = useFormContext<MaterialDetail>()

  const materialDetail = useSelector(materialStore.selectDetail)
  const userJob = useSelector(commonStore.selectUserValueJob)

  const handleChangeDrawingId = (event, optionValue: DataForDropdown) => {
    console.warn('optionValue', '>>>', optionValue)
  }

  return (
    <Grid container spacing={2}>
      <Grid item xs={6}>
        <Grid container spacing={2}>
          <Grid item xs={12} style={{ marginTop: 8 }}>
            <AppAutocomplete
              helperText={materialForm.errors.job_id?.message}
              error={Boolean(materialForm.errors.job_id)}
              value={userJob}
              popupIcon={null}
              disabled
              label="Job"
              required
              primaryKeyOption="value"
              options={[]}
              renderOption={(option) => option.description}
            />
          </Grid>
          <Grid item xs={12}>
            <AppAutocompleteAsync
              helperText={materialForm.errors.drawing_id?.message}
              error={Boolean(materialForm.errors.drawing_id)}
              // value={specificationDetail.drawing_id}
              compName="drawing_list"
              additionalData={{ limit_to_job: userJob.value }}
              required
              label="Schematic #"
              onChange={handleChangeDrawingId}
            />
          </Grid>

          <Grid item xs={12}>
            <FormControllerTextField
              disabled={false}
              control={materialForm.control}
              label="Element #"
              required
              name="spec_id"
            />
          </Grid>

          <Grid item xs={2}>
            <FormControllerTextField
              disabled={false}
              control={materialForm.control}
              label="Tag #"
              required
              name="spec_id"
            />
          </Grid>

          <Grid item xs={2}>
            <FormControllerTextField disabled={false} control={materialForm.control} label="Part #" name="spec_id" />
          </Grid>
        </Grid>
      </Grid>

      <Grid item xs={6}>
        <TableMultilingualDescription
          languageList={[]}
          editMode={false}
          name="descriptions"
          tableHeight={160}
          control={materialForm.control}
          editor="rte"
          copyable
          copyDialogProps={{
            compName: 'specification_list',
            entity: 'specifications'
          }}
          autocompleteProps={{
            getOptionLabel: (option) => option.entity_id
          }}
        />
      </Grid>
    </Grid>
  )
}

export default TabGeneral
