import { useEffect, useMemo } from 'react'
import { useForm } from 'react-hook-form'
import { useTranslation } from 'react-i18next'
import { useDispatch, useSelector } from 'react-redux'

import { DialogMain, FormControllerTabs, BtnHelp } from '@/components'
import TabGeneral from './TabGeneral'

import { commonStore, materialStore } from '@/store/reducers'
import { yupResolver } from '@hookform/resolvers/yup'
import _ from 'lodash'
import getValidationSchema from './validationSchema'

const DialogMaterialCE = () => {
  const { t } = useTranslation('material')

  const dispatch = useDispatch()

  const dialogState = useSelector(materialStore.selectDialogState)
  const userDrawing = useSelector(commonStore.selectUserValueDrawing)

  const vaidationSchema = useMemo(() => getValidationSchema(t), [])
  const specificationForm = useForm<any>({
    shouldUnregister: false,
    resolver: yupResolver(vaidationSchema)
  })

  useEffect(() => {
    specificationForm.reset(specificationDetail)
  }, [specificationDetail])

  useEffect(() => {
    if (userDrawing.value === -1) {
      dispatch(materialStore.actions.setDetail({ drawing_id: null }))
    } else {
      dispatch(materialStore.actions.setDetail({ drawing_id: userDrawing }))
    }
  }, [userDrawing])

  const handleSubmitForm = specificationForm.handleSubmit((formData) => {
    console.warn('formData', '>>>', formData)
  })

  const handleClose = () => {
    dispatch(materialStore.actions.setDialogStateOpen(false))
  }

  const handleChangeTab = (event, nextTab: number) => {
    dispatch(materialStore.actions.setDialogStateTab(nextTab))
  }

  const generalTab = {
    label: t('common:tab.general'),
    panel: <TabGeneral />,
    errorKey: ['drawing_id', 'spec_id', 'job_id']
  }

  const tabs = [generalTab]

  return (
    <DialogMain
      fullWidth
      maxWidth="md"
      open={dialogState.open}
      loading={dialogState.loading}
      title={<BtnHelp title={t('form.title.create_material')} href="" />}
      onOk={handleSubmitForm}
      onClose={handleClose}
      okText={t('common:button.create')}
    >
      <FormControllerTabs form={specificationForm} value={dialogState.tab} onChange={handleChangeTab} tabs={tabs} />
    </DialogMain>
  )
}

export default DialogMaterialCE
