import * as yup from 'yup';
import _ from 'lodash';

var tagSchema = function tagSchema(t) {
  return yup.object({
    element_id: yup.string().required(t('validation_message.element_id_required')).matches(/^\d{4}$/, t('validation_message.element_id_match')).transform(function (value) {
      return _.toUpper(value);
    }),
    schematic_id: yup.mixed().test('drawing_required', t('validation_message.schematic_id_required'), function (value) {
      return _.isNull(value) === false;
    }),
    job_id: yup.number().nullable().required(t('validation_message.job_id_required'))
  });
};

export default tagSchema;
