import Http from '@/utils/Http';
var commonApi = {
  getInitData: function getInitData(request) {
    return Http.get('init_data', request);
  },
  startBackgroundJob: function startBackgroundJob(type, operation_data, file) {
    var formData = new FormData();
    var background_operation_data = JSON.stringify({
      type: type,
      operation_data: operation_data
    });
    formData.append('background_operation_data', background_operation_data);
    formData.append('background_operation_file', file);
    return Http.post('background_operation/start', formData);
  },
  stopBackgroundJob: function stopBackgroundJob(operation_id) {
    return Http.put('background_operation/stop', {
      operation_id: operation_id
    });
  },
  sendReportMail: function sendReportMail(data) {
    return Http.post('send_report_mail', data);
  }
};
export default commonApi;
