import { createSlice, createAction, createSelector } from '@reduxjs/toolkit';
import { actionTypes } from '@/utils/constant';
export const name = 'item';
export const resetState = createAction(`${name}/${actionTypes.RESET_STATE}`);
export const initialState = {
  dataList: [],
  initDataForList: {
    permissions: {
      item: null
    },
    jobs: [],
    wiki_page: '',
    column_tooltips: {}
  }
};
const itemSlice = createSlice({
  name,
  initialState,
  reducers: {
    setDataList(state, {
      payload
    }) {
      state.dataList = payload;
    },

    setInitDataForList(state, {
      payload
    }) {
      state.initDataForList = payload;
    }

  },
  extraReducers: {
    [resetState.type]() {
      return initialState;
    }

  }
}); // Actions

export const {
  actions
} = itemSlice; // Saga actions

export const sagaGetList = createAction(`${name}/${actionTypes.GET_LIST}`);
export const sagaChangeUserJob = createAction(`${name}/${actionTypes.CHANGE_USER_JOB}`);
export const sagaRemove = createAction(`${name}/${actionTypes.REMOVE}`);
export const sagaChangeUserDrawing = createAction(`${name}/${actionTypes.CHANGE_USER_DRAWING}`); // selector

const selectState = state => state[name];

export const selectDataList = createSelector(selectState, state => state.dataList);
export const selectPermissions = createSelector(selectState, state => state.initDataForList.permissions.item);
export const selectInitDataForList = createSelector(selectState, state => state.initDataForList);
export default itemSlice;
