import * as yup from 'yup';
import _ from 'lodash';

const tagSchema = t => yup.object({
  element_id: yup.string().required(t('validation_message.element_id_required')).matches(/^\d{4}$/, t('validation_message.element_id_match')).transform(value => _.toUpper(value)),
  schematic_id: yup.mixed().test('drawing_required', t('validation_message.schematic_id_required'), value => _.isNull(value) === false),
  job_id: yup.number().nullable().required(t('validation_message.job_id_required')),
  tag: yup.string().required(t('validation_message.tag_required'))
});

export default tagSchema;
