import { createSlice } from '@reduxjs/toolkit';
export const name = 'summaryReport';
export const initialState = {
  open: false,
  title: 'Summary report',
  numberOfSuccess: 0,
  numberOfFailed: 0,
  failedReasons: []
};
const summaryReport = createSlice({
  name,
  initialState,
  reducers: {
    setOpen(state, {
      payload
    }) {
      state.open = payload;
    },

    setReportData(state, {
      payload
    }) {
      state.open = false;
      state.numberOfFailed = payload.failed_count;
      state.numberOfSuccess = payload.success_count;
      state.failedReasons = payload.failed_reasons;
    }

  }
}); // Actions

export const {
  setOpen,
  setReportData
} = summaryReport.actions; // Selectors

export const selectState = state => state.summaryReport;
export default summaryReport.reducer;
