import { useEffect, useMemo } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import { DataGrid, GridColumns } from '@material-ui/data-grid'

import useAuthMiddleware from '@/hooks/useAuthMiddleware'

import {
  AppLayout,
  EditIcon,
  CreateIcon,
  DialogMaterialCE,
  AppAutocompleteStyled,
  DeleteIcon,
  SaveIcon,
  MakeAListIcon
} from '@/components'

import { useTranslation } from 'next-i18next'
import { materialStore, commonStore } from '@/store/reducers'
import { defaultProperties, iconColumn } from '@/utils/columnProperties'

import type { ParameterOption } from '@/types/Common'
import useStyles from '@/styles/page/layout'

const SpecificationsPage = () => {
  const classes = useStyles()
  const { t } = useTranslation('material')

  const breadcrumbData = useMemo(
    () => [
      { label: 'Home', href: '/' },
      { label: 'Drawings', href: '/drawings' },
      { label: 'Material List', href: '/materials' }
    ],
    []
  )

  const dispatch = useDispatch()
  const permissions = useSelector(materialStore.selectPermissions)
  const userJob = useSelector(commonStore.selectUserValueJob)
  // const materialSum = useSelector(materialStore.selectMaterialSum)

  useEffect(() => {
    return () => dispatch(commonStore.actions.resetUserValue())
  }, [])

  const handleOpenCreateDialog = () => {
    dispatch(materialStore.actions.setDialogStateOpen(true))
  }

  const handleChangeUserJob = (event, optionValue: ParameterOption) => {
    console.warn('optionValue', '>>>', optionValue)
  }

  const columns: GridColumns = [
    {
      ...defaultProperties,
      ...iconColumn,
      field: 'id',
      headerName: 'Edit',
      renderCell() {
        return <EditIcon />
      }
    },
    {
      ...defaultProperties,
      field: 'part_id',
      headerName: 'Part #',
      flex: 0.2
    },
    {
      ...defaultProperties,
      field: 'description',
      headerName: 'Description',
      flex: 0.3
    },
    {
      ...defaultProperties,
      field: 'MASS',
      headerName: 'U.MASS(Kg)',
      flex: 0.1
    },
    {
      ...defaultProperties,
      field: 'INST',
      headerName: 'INST',
      flex: 0.1
    },
    {
      ...defaultProperties,
      field: 'SPAR',
      headerName: 'SPAR',
      flex: 0.1
    },
    {
      ...defaultProperties,
      field: 'COM',
      headerName: 'COM',
      flex: 0.1
    },
    {
      ...defaultProperties,
      field: 'REP',
      headerName: 'REP',
      flex: 0.1
    },
    {
      ...defaultProperties,
      field: 'EREC',
      headerName: 'EREC',
      flex: 0.1
    }
  ]

  const buttonsPage = [
    {
      label: t('common:button.new'),
      startIcon: <CreateIcon />,
      onClick: handleOpenCreateDialog
    },
    {
      label: t('common:button.save'),
      startIcon: <SaveIcon />
    },
    {
      label: t('common:button.delete'),
      startIcon: <DeleteIcon />
    },
    {
      label: t('common:button.make_a_list'),
      startIcon: <MakeAListIcon />
    }
  ]

  const Options = (
    <>
      <AppAutocompleteStyled
        className={classes.controlAutocomplete}
        width={250}
        label="Jobs"
        options={[]}
        value={userJob}
        renderOption={(option) => option.description}
        primaryKeyOption="value"
        onChange={handleChangeUserJob}
      />
    </>
  )

  const Dialogs = (
    <>
      <DialogMaterialCE />
    </>
  )

  const bottomSection = (
    <div style={{ height: 35, width: '100%' }}>
      <DataGrid
        disableSelectionOnClick
        disableColumnMenu
        checkboxSelection
        hideFooter
        columns={columns}
        headerHeight={0}
        rowHeight={35}
        getRowClassName={() => classes.totalBudgetRow}
        rows={[materialSum]}
        onCellDoubleClick={(_, event) => {
          event.stopPropagation()
        }}
      />
    </div>
  )

  return (
    <AppLayout
      entity="materials"
      breadcrumbs={breadcrumbData}
      searchProps={{
        width: 320
      }}
      buttons={buttonsPage}
      permissions={permissions}
      tableProps={{
        columns
      }}
      Dialogs={Dialogs}
      Options={Options}
      bottomSection={bottomSection}
    />
  )
}

export const getServerSideProps = useAuthMiddleware([
  'common',
  'materials',
  'make_a_list',
  'advanced_filter',
  'drawing'
])

export default SpecificationsPage
