import { actionTypes } from '@/utils/constant'
import { createAction, createSlice, createSelector, PayloadAction } from '@reduxjs/toolkit'

import type { Entity } from '@/types/Common'
import type { RootReducerType } from './rootReducer'

export const name: Entity = 'material'
export const resetState = createAction(`${name}/${actionTypes.RESET_STATE}`)

export const materialDetail = {
  job_id: null,
  drawing_id: null,
  dpn: '',
  unit: null,
  descriptions: [],
  items: [],
  manufacturers: []
}

const initialState = {
  initDataForList: {
    permissions: {
      material: {
        view: true
      }
    }
  },
  dataList: [
    {
      id: '1',
      part_id: '1111 000004 H001',
      description: 'TEST',
      MASS: '100',
      INST: '1',
      SPAR: '1',
      COM: '1',
      REP: '1',
      EREC: '1'
    },
    {
      id: '2',
      part_id: '1111 000004 H002',
      description: 'TEST_2',
      MASS: '200',
      INST: '2',
      SPAR: '2',
      COM: '2',
      REP: '2',
      EREC: '2'
    },
    {
      id: '3',
      part_id: '1111 000004 H003',
      description: 'TEST_3',
      MASS: '300',
      INST: '3',
      SPAR: '3',
      COM: '3',
      REP: '3',
      EREC: '3'
    }
  ],
  initDataForCE: {
    parameters: {},
    wiki_page: ''
  },
  dialogState: {
    open: false,
    loading: false,
    tab: 0
  },
  detail: materialDetail,
  materialSum: {
    amount: 0,
    currency_id: '',
    description: 'Total of selected cost codes',
    left_in_order: 0,
    left_in_rfq: 0,
    parameter_id: '',
    used_in_order: 0,
    used_in_rfq: 0
  }
}

const materialSlice = createSlice({
  name,
  initialState,
  reducers: {
    setDataList(state, { payload }) {
      state.dataList = payload
    },
    setDialogState(state, { payload }) {
      state.dialogState = { ...state.dialogState, ...payload }
    },
    setDialogStateOpen(state, { payload }: PayloadAction<boolean>) {
      state.dialogState.open = payload
    },
    setDialogStateLoading(state, { payload }: PayloadAction<boolean>) {
      state.dialogState.loading = payload
    },
    setDetail(state, { payload }) {
      state.detail = {
        ...state.detail,
        ...payload
      }
    },
    setDialogStateTab(state, { payload }: PayloadAction<number>) {
      state.dialogState.tab = payload
    }
  },
  extraReducers: {
    [resetState.type]() {
      return initialState
    }
  }
})

export const { actions } = materialSlice

export const selectState = (state: RootReducerType) => state[name]
export const selectPermissions = createSelector(selectState, (state) => state.initDataForList.permissions.material)
export const selectDialogState = createSelector(selectState, (state) => state.dialogState)
export const selectDetail = createSelector(selectState, (state) => state.detail)
export const selectDataList = createSelector(selectState, (state) => state.dataList)

export default materialSlice
