import { useFormContext } from 'react-hook-form'
import { useSelector } from 'react-redux'

import { Grid } from '@material-ui/core'
import {
  DataTable,
  AppAutocompleteAsync,
  FormControllerTextField,
  SectionTimezone,
  AppAutocomplete
} from '@/components'
import { When } from 'react-if'

import { tagStore, commonStore } from '@/store/reducers'

import type { SpecificationDetail } from '@/types/Specification'
import type { DataForDropdown } from '@/types/Common'

const TabGeneral = () => {
  const specificationForm = useFormContext<SpecificationDetail>()

  const specificationDetail = useSelector(tagStore.selectDetail)
  const userJob = useSelector(commonStore.selectUserValueJob)

  const handleChangeDrawingId = (event, optionValue: DataForDropdown) => {
    console.warn('optionValue', '>>>', optionValue)
  }

  const columns: GridColumns = [
    {
      ...defaultProperties,
      field: 'raw_item_id',
      headerName: 'Item',
      flex: 0.15,
      renderCell(params) {
        const { value, id } = params
        return (
          <Link href="#" onClick={handleOpenPartDialog(id as number, value as string)}>
            {value}
          </Link>
        )
      }
    },
    {
      ...editCell('Quantity'),
      ...rightColumn,
      field: 'quantity',
      flex: 0.15,
      sortable: false,
      valueGetter(params) {
        const item = _.find(watchItems, { item_id: params.id }) as any
        return item?.quantity || 0
      },
      valueFormatter(params) {
        return AppNumber.format(params.value, itemQuantityFormat)
      },
      renderEditCell(params) {
        return (
          <DataTableNumberField
            params={params}
            onChangeValue={handleChangeQuantity}
            rules={yup
              .number()
              .nullable()
              .required(t('validation_message.quantity_range'))
              .min(itemQuantityFormat.min, t('validation_message.quantity_range'))
              .max(itemQuantityFormat.max, t('validation_message.quantity_range'))}
          />
        )
      }
    },
    {
      ...rightColumn,
      sortable: false,
      field: 'mass',
      headerName: 'Unit Mass (kg)',
      flex: 0.15,
      valueFormatter(params) {
        return AppNumber.format(params.value, itemQuantityFormat)
      }
    }
  ]

  return (
    <Grid container spacing={2}>
      <Grid item xs={6}>
        <Grid container spacing={2}>
          <Grid item xs={12} style={{ marginTop: 8 }}>
            <AppAutocomplete
              helperText={specificationForm.errors.job_id?.message}
              error={Boolean(specificationForm.errors.job_id)}
              value={userJob}
              popupIcon={null}
              disabled
              label="Job"
              required
              primaryKeyOption="value"
              options={[]}
              renderOption={(option) => option.description}
            />
          </Grid>
          <Grid item xs={12}>
            <AppAutocompleteAsync
              helperText={specificationForm.errors.drawing_id?.message}
              error={Boolean(specificationForm.errors.drawing_id)}
              // value={specificationDetail.drawing_id}
              compName="drawing_list"
              additionalData={{ limit_to_job: userJob.value }}
              required
              label="Part #"
              onChange={handleChangeDrawingId}
            />
          </Grid>

          <Grid item xs={12}>
            <FormControllerTextField
              disabled={false}
              control={specificationForm.control}
              label="Description #"
              required
              name="spec_id"
            />
          </Grid>

          <Grid item xs={6}>
            <FormControllerTextField
              disabled={false}
              control={specificationForm.control}
              label="Mass kg"
              required
              name="spec_id"
            />
          </Grid>

          <Grid item xs={6}>
            <FormControllerTextField
              disabled={false}
              control={specificationForm.control}
              label="Unit #"
              name="spec_id"
            />
          </Grid>

          <When condition={false}>
            <SectionTimezone value={specificationDetail} />
          </When>
        </Grid>
      </Grid>

      <Grid item xs={6}>
        <DataTable
          hideFooter
          disableSelectionOnClick
          disableColumnMenu
          tableHeight={tableHeight}
          rows={filteredLanguageList}
          columns={columns}
          onCellDoubleClick={onDbClickCell}
          getRowClassName={getStylesRowDisabled}
        />
      </Grid>
    </Grid>
  )
}

export default TabGeneral
