import { useFormContext } from 'react-hook-form'
import { useSelector } from 'react-redux'

import { Grid, Paper } from '@material-ui/core'
import {
  DataTable,
  AppAutocompleteAsync,
  FormControllerTextField,
  SectionTimezone,
  AppAutocomplete
} from '@/components'
import { When } from 'react-if'

import type { GridColumns } from '@material-ui/data-grid'

import { materialStore, specificationStore, commonStore } from '@/store/reducers'
import { defaultProperties, editCell } from '@/utils/columnProperties'

import type { SpecificationDetail } from '@/types/Specification'
import type { DataForDropdown } from '@/types/Common'

const TabGeneral = () => {
  const materialsForm = useFormContext<SpecificationDetail>()

  const materialDetail = useSelector(specificationStore.selectDetail)
  const userJob = useSelector(commonStore.selectUserValueJob)
  const materialQuantity = useSelector(materialStore.selectMaterialQuantity)

  const handleChangeDrawingId = (event, optionValue: DataForDropdown) => {
    console.warn('optionValue', '>>>', optionValue)
  }

  const columns = [
    // { field: 'id', headerName: 'DT_RowId(ID)', width: 150 },
    { field: 'quantity', headerName: 'Quantity' },
    { field: 'usage', headerName: 'Usage', width: 130 }
  ]

  const rows = [
    {
      id: 1,
      quantity: 0,
      usage: 'Installed Parts'
    },
    {
      id: 2,
      quantity: 0,
      usage: 'Spare Parts'
    },
    {
      id: 3,
      quantity: 0,
      usage: 'Commissioning Parts'
    },
    {
      id: 4,
      quantity: 0,
      usage: 'Replace Parts'
    },
    {
      id: 5,
      quantity: 0,
      usage: 'Erection Commissioning and Supervision'
    }
  ]

  return (
    <Grid container spacing={2}>
      <Grid item xs={6}>
        <Grid container spacing={2}>
          <Grid item xs={12} style={{ marginTop: 8 }}>
            <AppAutocomplete
              helperText={materialsForm.errors.job_id?.message}
              error={Boolean(materialsForm.errors.job_id)}
              value={userJob}
              popupIcon={null}
              disabled
              label="Job"
              required
              primaryKeyOption="value"
              options={[]}
              renderOption={(option) => option.description}
            />
          </Grid>
          <Grid item xs={12}>
            <AppAutocompleteAsync
              helperText={materialsForm.errors.drawing_id?.message}
              error={Boolean(materialsForm.errors.drawing_id)}
              compName="part_list"
              additionalData={{ limit_to_job: userJob.value }}
              required
              label="Part #"
              onChange={handleChangeDrawingId}
            />
          </Grid>

          <Grid item xs={12}>
            <FormControllerTextField
              disabled={false}
              control={materialsForm.control}
              label="Description #"
              required
              name="spec_id"
            />
          </Grid>

          <Grid item xs={6}>
            <FormControllerTextField
              disabled={false}
              control={materialsForm.control}
              label="Mass kg"
              required
              name="Mass"
            />
          </Grid>

          <Grid item xs={6}>
            <FormControllerTextField disabled={false} control={materialsForm.control} label="Unit #" name="spec_id" />
          </Grid>

          <When condition={false}>
            <SectionTimezone value={materialDetail} />
          </When>
        </Grid>
      </Grid>

      <Grid item xs={6}>
        <Paper elevation={1}>
          <DataTable
            hideFooter
            disableSelectionOnClick
            disableColumnMenu
            tableHeight={250}
            // rows={[materialQuantity]}
            // getRowId={(r) => r.id}
            // columns={[
            //   {
            //     // ...editCell('Quantity'),
            //     // ...rightColumn,
            //     headerName: 'Quantity',
            //     field: 'quantity',
            //     flex: 0.1,
            //     sortable: false
            //   },
            //   {
            //     ...defaultProperties,
            //     field: 'usage',
            //     headerName: 'Usage',
            //     flex: 0.3
            //   }
            // ]}
            getRowId={(r) => r.id}
            rows={rows}
            columns={columns}
            pageSize={5}
          />
        </Paper>
      </Grid>
    </Grid>
  )
}

export default TabGeneral
