import { useEffect, useMemo } from 'react'
import { useForm } from 'react-hook-form'
import { useTranslation } from 'react-i18next'
import { useDispatch, useSelector } from 'react-redux'

import { DialogMain, FormControllerTabs, BtnHelp, AppTabHistoryLog, useConfirm } from '@/components'
import TabGeneral from './TabGeneral'

import _ from 'lodash'
import { tagStore } from '@/store/reducers'
import { yupResolver } from '@hookform/resolvers/yup'
import getValidationSchema from './validationSchema'

import type { TagDetail } from '@/types/Tag'
import type { DataForDropdown, HistoryLog } from '@/types/Common'

const DialogTag = () => {
  const { t } = useTranslation('element')
  const { confirm } = useConfirm()

  const dispatch = useDispatch()

  const dialogState = useSelector(tagStore.selectDialogState)
  const tagDetail = useSelector(tagStore.selectDetail)
  const { wiki_page } = useSelector(tagStore.selectInitDataForCE)
  const permissions = useSelector(tagStore.selectPermissions)
  const confirmRef = useSelector(tagStore.selectConfirmRef)

  const isCreating = _.isNil(tagDetail.id)

  const vaidationSchema = useMemo(() => getValidationSchema(t), [])
  const tagForm = useForm<TagDetail>({
    shouldUnregister: false,
    resolver: yupResolver(vaidationSchema)
  })

  useEffect(() => {
    tagForm.reset(tagDetail)
  }, [tagDetail])

  useEffect(() => {
    if (confirmRef.open) {
      handleConfirmRef()
    }
  }, [confirmRef.open])

  const handleConfirmRef = async () => {
    const result = await confirm({
      title: 'Warning',
      description: confirmRef.message,
      buttons: [{ label: 'Ok', action: 'OK' }]
    })
    if (result === 'OK') {
      dispatch(tagStore.actions.setConfirmRefOpen(false))
    }
  }

  const handleSubmitForm = tagForm.handleSubmit((formData) => {
    formData.schematic_id = (formData.schematic_id as DataForDropdown).value
    formData.part_id = (formData.part_id as DataForDropdown)?.value || null
    const data = _.pick(formData, [
      'job_id',
      'schematic_id',
      'element_id',
      'part_id',
      'tag',
      'fluid',
      'tech_data',
      'descriptions',
      'functions',
      'additional_attributes'
    ])
    if (isCreating) {
      dispatch(tagStore.sagaCreate(data))
    } else {
      const tagEdit = _(formData)
        .assign({ element_id_pk: formData.id })
        .pick([
          'fluid',
          'tech_data',
          'descriptions',
          'functions',
          'tag',
          'element_id',
          'part_id',
          'element_id_pk',
          'additional_attributes'
        ])
        .value()
      dispatch(tagStore.sagaUpdateMultiple([tagEdit as TagDetail]))
    }
  })

  const handleClose = () => {
    dispatch(tagStore.sagaCloseDialog())
  }

  const handleChangeTab = (event, nextTab: number) => {
    dispatch(tagStore.actions.setDialogStateTab(nextTab))
  }

  const handleChangeHistoryLogs = (data: HistoryLog[]) => {
    dispatch(tagStore.actions.setHistoryLogs(data))
  }

  const generalTab = {
    label: t('common:tab.general'),
    panel: <TabGeneral />,
    errorKey: ['schematic_id', 'element_id ', 'job_id', 'part_id', 'tag', 'fluid', 'tech_data', 'functions']
  }

  const historyTab = {
    label: t('common:tab.history'),
    panel: (
      <AppTabHistoryLog
        isOpenDialog={dialogState.open}
        data={dialogState.historyLogs}
        onChange={handleChangeHistoryLogs}
        entityId={tagDetail.id}
        mode="horizonatal"
        tableHeight={430}
        descriptionRows={26}
        entity="element"
      />
    ),
    disabled: isCreating
  }

  const tabs = [generalTab, historyTab]

  return (
    <DialogMain
      fullWidth
      maxWidth="md"
      open={dialogState.open}
      loading={dialogState.loading}
      title={<BtnHelp title={isCreating ? t('form.title.create_tag') : t('form.title.update_tag')} href={wiki_page} />}
      onOk={handleSubmitForm}
      onClose={handleClose}
      okText={isCreating ? t('common:button.create') : t('common:button.update')}
      okButtonProps={{
        disabled: dialogState.loading || (!permissions?.edit && !isCreating)
      }}
    >
      <FormControllerTabs form={tagForm} value={dialogState.tab} onChange={handleChangeTab} tabs={tabs} />
    </DialogMain>
  )
}

export default DialogTag
